#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Web Dev\PDF_chat\server\node_modules\.pnpm\openai@4.96.2_ws@8.18.1_zod@3.24.3\node_modules\openai\bin\node_modules;C:\Web Dev\PDF_chat\server\node_modules\.pnpm\openai@4.96.2_ws@8.18.1_zod@3.24.3\node_modules\openai\node_modules;C:\Web Dev\PDF_chat\server\node_modules\.pnpm\openai@4.96.2_ws@8.18.1_zod@3.24.3\node_modules;C:\Web Dev\PDF_chat\server\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Web Dev/PDF_chat/server/node_modules/.pnpm/openai@4.96.2_ws@8.18.1_zod@3.24.3/node_modules/openai/bin/node_modules:/mnt/c/Web Dev/PDF_chat/server/node_modules/.pnpm/openai@4.96.2_ws@8.18.1_zod@3.24.3/node_modules/openai/node_modules:/mnt/c/Web Dev/PDF_chat/server/node_modules/.pnpm/openai@4.96.2_ws@8.18.1_zod@3.24.3/node_modules:/mnt/c/Web Dev/PDF_chat/server/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../openai/bin/cli" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../openai/bin/cli" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../openai/bin/cli" $args
  } else {
    & "node$exe"  "$basedir/../openai/bin/cli" $args
  }
